function outer() {
  let counter = 0; // локальна змінна зовнішньої функції

  return function inner() {
    // внутрішня функція
    counter++; // доступ до змінної outer()
    console.log(counter);
  };
}

const increment = outer(); // викликаємо outer, що повертає функцію inner
increment(); // виведе 1
increment(); // виведе 2

